/**
   * name   : Dark WhatsApp Theme by Mew
   * info   : A customizable theme for WhatsApp.
   * repo   : https://github.com/vednoc/onyx
   * discord: https://discord.gg/NpT8PzA
   * version: 1.2.3
   * author : vednoc
   * license: MIT
   */
  :root {
    --dark:    #272C35;
    --darker:  #1F232A;
    --light:   #D1D1D1;
    --lighter: #E9E9E9;
    --accent:  #7289DA;
    --icon:    #E1E1E1;
    --shadow:  rgba(0,0,0,0.10);
    --mred:    #A3525B;
    --mgreen:  #70A352;
    --mblue:   #527AA3;
    --image: url(https://media.discordapp.net/attachments/310799529161129984/417408747812552715/zilesanamin.jpg);
    --emojiOpacity: 0.75;
  }

  /* Body reset. */
  body {
    background: none;
    background-color: var(--dark); }

  /* Scrollbar track. */
  *::-webkit-scrollbar-track {
    background-color: var(--shadow); }
  /* Scrollbar thumb. */
  *::-webkit-scrollbar-thumb {
    background-color: rgba(0,0,0,0.2); }
  /* Highlight selection. */
  ::selection {
    color: var(--light) !important;
    background-color: var(--accent) !important; }
  ::-moz-selection {
    color: var(--light) !important;
    background-color: var(--accent) !important; }
  /* Input placeholders. */
  ::-webkit-input-placeholder {
    color: var(--lighter) !important; }

  /* Progress. */
  progress[value]::-webkit-progress-bar {
    background: var(--darker); }
  /* Value. */
  progress[value]::-webkit-progress-value {
    background: var(--accent); }

  /* App wrapper. */
  #app .app {
    background: none;
    background-color: var(--dark); }

    /* Accent background. */
    .app-wrapper::after {
      background: none !important; }


  /* Log-in page. */
  ._2NbD3 {
    border: 1px solid var(--shadow);
    box-shadow: 0 8px 32px 0 var(--shadow) !important;
    background-color: var(--darker) !important; }
    /* Text color. */
    ._2NbD3 * {
      color: var(--light); }
    /* Reload QR code. */
    .HnNfm {
      background-color: var(--darker) !important; }
    /* Fix QR code. */
    ._2NbD3 .XSdna {
      border: 3px solid white !important; }
    /* Tutorial. */
    ._2NbD3 > div:last-child {
      background-color: var(--darker); }

  /* Disable background. */
  ._2U_Zc:before {
    display: none !important; }

  /* Icon color. */
  span[class="_3CSsZ"] > svg > rect {
    fill: var(--dark) !important; }


  /* Landing page. */
  .app ._1Iexl ._3qlW9 {
    /* box-shadow: 1px -3px 2px 0 var(--shadow) inset; */
    border: none;
    background-color: var(--dark); }
    /* Status update. */
    .app ._3q4NP ._3qlW9::before {
      line-height: 25px;
      content: "Updated on 21st of November. See the changelog at: https://github.com/vednoc/onyx/releases";
      left: 0;
      top: 0;
      bottom: 6px;
      right: 0;
      position: absolute;
      z-index: 98;
      width: 90%;
      margin: 0 5%;
      padding-top: 40vh;
      text-align: center;
      font-size: 20px;
      color: var(--light);
      background: var(--dark); } /**/
    /* Image. */
    .app ._1Iexl ._3qlW9 ._3BqNZ ._2Uo0Z {
      max-width: 200px;
      max-height: 200px;
      border-radius: 50%;
      filter: opacity(0.75);
      transition: opacity 1s ease !important; }
    /* Text color. */
    .app ._1Iexl ._3qlW9 ._2MnNk * {
      color: var(--light); }
    /* Accent bar. */
    .app ._1Iexl ._3qlW9:after {
      border-top-color: var(--accent); }

  /* Left drawer. */
  #app .app #side {
    background-color: var(--darker); }

    /* Chat list. */
    #side > header {
      background-color: var(--darker) !important; }

    /* Notifications && more!! */
    #side ._3YewW {
      box-shadow: inset 0 0 4px 0 var(--shadow);
      background-color: var(--accent) !important; }
      /* Text color. */
      #side ._3YewW .l2BEH * {
